#定义函数，根据url获取网页
def getpage(url):
    req = urllib2.Request( url )#建一个对象
    con = urllib2.urlopen( req )#建立连接
    data = con.read()#读取网页数据
    con.close()#关闭连接
    return data#返回网页数据

def getdata(url,file,school,region):
    data = getpage(url)
    soup = BeautifulSoup(data)
    if not soup.body.find(id='demotable1'):
        return
    tbody = soup.body.find(id='demotable1').tbody
    for tr in tbody.find_all('tr'):
        temp = school + ',' + region + ','
        for td in tr.find_all('td'):
            temp += td.get_text().encode('utf8') + ','
        temp = temp.replace(" ", "") + '\n'
        #print temp
        file.write(temp)

